import React, { useState } from 'react';

function BookingForm({availableTimes, dispatch}) {
  const [formData, setFormData] = useState({
    date: '',
    time: '',
    guests: '',
    occasion: 'Birthday', // Default occasion
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    if (name === 'date') {
      // Se a mudança foi na data, você deve despachar a ação para atualizar os horários com base na data
      dispatch(value); // Disparar a mudança de estado com a nova data selecionada
    }
  };



  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Date:
      </label>
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
        />

      <label>
        Time:
        </label>
        <select
          name="time"
          value={formData.time}
          onChange={handleChange}
        >
        {availableTimes.map((time) => (
          <option key={time} value={time}>
          {time}
          </option>))}
        </select>


      <label>
        Number of Guests:
        </label>
        <input
          type="number"
          name="guests"
          value={formData.guests}
          onChange={handleChange}
        />


      <label>
        Occasion:
        </label>
        <select name="occasion" value={formData.occasion} onChange={handleChange}>
          <option value="Birthday">Birthday</option>
          <option value="Anniversary">Anniversary</option>
        </select>


      <button type="submit">Submit Reservation</button>
    </form>
  );

};

export default BookingForm;
