import React from "react";
import BookingForm from "../BookingForm";
import Spacer from "../Spacer-footer";
import { useState, useReducer } from 'react';

const availableTimes = initializeTimes();
const setAvailableTimes = (newAvailableTimes) => {
  availableTimes = newAvailableTimes;
};

function updateTimes() {
    return(
        {availableTimes}
    )
}

function initializeTimes() {
    return ['10:00 AM', '12:00 PM', '2:00 PM', '4:00 PM', '6:00 PM', '10:00 PM', '11:00 PM'];
  }


const BookingPage = (props) => {

    const [availableTimes, dispatch] = useReducer(updateTimes, initializeTimes());

    return (
        <>
            <Spacer/>
            <BookingForm availableTimes={availableTimes} setAvailableTimes={setAvailableTimes} dispatch={dispatch}/>
        </>
    )
}

export default BookingPage;
