    //     '4:00 PM',
    //     '6:00 PM',
    //     '10:00 PM',
    //     '11:00 PM',
    //   ]);


    return (
        <>
            <Spacer/>
            <BookingForm availableTimes={availableTimes} updateTimes={dispatch}/>
        </>
    )
}

export default BookingPage;
